import {
  SEARCH_FILTER, NUMBER_FILTER, COLUMN_FILTER, OPERATION_FILTER,
} from '../actions';

const filterInitial = {
  filterByName: {
    name: '',
  },
  filterByNumber: {
    column: '',
    operation: '',
    number: '',
  }
};
const filters = (state = filterInitial, action) => {
  switch (action.type) {
    case SEARCH_FILTER:
      return {
        ...state,
        filterByName: {
          name: action.payload,
        },
      };
    case NUMBER_FILTER:
      return {
        ...state,
        filterByNumber: {
          number: action.payload,
          operation,
          column,
        },
      }
    case OPERATION_FILTER:
      return {
        ...state,
        filterByNumber: {
          operation: action.payload,
          number,
          column,
        }
      }
    case COLUMN_FILTER:
      return {
        ...state,
        filterByNumber: {
          column: action.payload,
          ...state.number,
          ...state.operation
        }
      }
    default:
      return state;
  }
};


export default filters;
