import React from 'react'
import { connect } from 'react-redux';
import { cFilter, oFilter, nFilter } from '../actions/';

class SelectsInputs extends React.Component {
  render(){
    const { cFilter, oFilter, nFilter } = this.props
    return (
      <div>
         <label htmlFor="selectColumn">Filtros</label>
         <select data-testid='column-filter' name="Coluna" id="selectColumn" form="selectForm" onChange={(event) => cFilter(event.target.value)}>
          <option>Coluna</option>
          <option value="population">population</option>
          <option value="orbital_period">orbital_period</option>
          <option value="diameter">diameter</option>
          <option value="rotation_period">rotation_period</option>
          <option value="surface_water">surface_water</option>
        </select>
         <br />
         <label htmlFor="selectMaior"></label>
         <select data-testid='comparison-filter'name="Comparação" id='selectMaior' form='selectForm'onChange={(event) => oFilter(event.target.value)}>
           <option>Comparação</option>
           <option value='maior que'>maior que</option>
           <option value='menor que'>menor que</option>
           <option value='igual a'>igual a</option>
         </select>
        <input type='number' data-testid='value-filter' onChange={(event) => nFilter(event.target.value)}/>
        <button type='submit' 
        value='submit' 
        data-testid='button-filter'

        >Filtrar</button>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
  setCurrentToken: (number) => dispatch(nFilter(number)),
  setImgPath: (operation) => dispatch(oFilter(operation)),
  setUser: (column) => dispatch(cFilter(column)),
});

export default connect(null, mapDispatchToProps)(SelectsInputs);
