import { combineReducers } from 'redux';


import {
  REQUEST_PLANETS,
  REQUEST_PLANETS_FAILURE,
  REQUEST_PLANETS_SUCCESS,
} from '../actions';

const CHECK_FETCH = {
  isFetching: false,
  data: [],
};
const swPlanetss = (state = CHECK_FETCH, action) => {
  switch (action.type) {
    case REQUEST_PLANETS:
      return {
        ...state,
        isFetching: true,
      };
    case REQUEST_PLANETS_SUCCESS:
      return {
        ...state,
        data: action.data.results,
        isFetching: false,
      };
    case REQUEST_PLANETS_FAILURE:
      return {
        ...state,
        error: action.error,
        isFetching: false,
      };
    default:
      return state;
  }
};


import {
  SEARCH_FILTER,
} from '../actions'

const filterInitial = {
  filterByName:{
    name:''
  }
}
const filters = (state = filterInitial, action) => {
  switch(action.type) {
    case SEARCH_FILTER:
      return {
        ...state,
        filterByName:{
          name: action.payload,
        }
      };
      default:
        return state
  }
}

const rootReducer = combineReducers({
  swPlanetss,filters
});
export default rootReducer;
