import React from 'react';
import { connect } from 'react-redux';
import { totalFilter } from '../actions/';

const cOptions = [
  'coluna',
  'population',
  'orbital_period',
  'diameter',
  'rotation_period',
  'surface_water',
];

class SelectsInputs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      coluna: '',
      comparacao: '',
      valor: 0,
    };
    this.handleCh = this.handleCh.bind(this);
  }
  handleCh(event) {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }
  render() {
    const { col, com, val } = this.state;
    const { filter } = this.props;
    let fCheck = cOptions;
    if (filter.length > 0) {
      filter.forEach((elements) => {
        if (fCheck.includes(elements.column)) {
          fCheck.splice(fCheck.indexOf(elements.column), 1);
        }
      });
    }
    return (
      <div>
        <label htmlFor="selectColumn">Filtros</label>
        <select data-testid="column-filter" name="coluna" id="selectColumn" onChange={this.handleCh}>
          {fCheck.map((elements) => (
            <option value={elements}>{elements}</option>
          ))}
        </select>
        <br />
        <label htmlFor="selectMaior"></label>
        <select data-testid="comparison-filter" name="comparacao" id="selectMaior" onChange={this.handleCh}>
          <option>Comparação</option>
          <option value="maior que">maior que</option>
          <option value="menor que">menor que</option>
          <option value="igual a">igual a</option>
        </select>
        <input type="number" name="valor" data-testid="value-filter" onChange={this.handleCh}/>
        <button type="button" value="submit" data-testid="button-filter" onClick={() => this.props.gFilter(col, com, val)}
        >
          Filtrar
        </button>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  filter: state.filters.filterByNumericValues,
});
const mapDispatchToProps = (dispatch) => ({
  gFilter: (a, b, c) => dispatch(totalFilter(a, b, c)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SelectsInputs);
