import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchPlanets } from '../actions';
import TableHeaders from './tableheaders';
import TableValues from './tableValues'

class Table extends React.Component {

  componentDidMount() {
    const { getCurrentPlanets } = this.props;
    // console.log('antes de chamar')
    getCurrentPlanets();
  }
  filterTest() {
    const { swPlanetss, cColumn, cOperation, cNumber } = this.props;
    let data = swPlanetss;
    if(cColumn !== undefined){
      data.filter((plan) => (
        plan.cColumn.includes(cColumn)
        ))
      if(cOperation === 'maior que')
        data.filter((plani) => (
          plani.cColumn > cNumber
        ))
      else if (cOperation === 'menor que')
          data.filter ((plani) => (
            plani.cColumn < cNumber
          ))
      else if (cOperation === 'igual a')
            data.filter ((plani) => (
              plani.cColumn === cNumber
            ))
    }
    console.log(data)

  }
  render() {
    const { swPlanetss, isFetching, textFilter } = this.props;
    if (isFetching) { return <p>Carregando...</p>; }
    return (
      <section>
        <button  
        value='submit' 
        data-testid='button-filter'
        onClick={() => this.filterTest()}
        >Filtrar</button>
        <table>
          <TableHeaders />
          <tbody>
          {swPlanetss
          .filter((planet) => (planet.name.toLowerCase().includes(textFilter.toLowerCase())))
          .map((elements) => (
            <TableValues key={elements.diameter} elements={ elements } />
          ))}
          </tbody>
        </table>
      </section>
    );
  }
}

const mapStateToProps = (state) => ({
  swPlanetss: state.swPlanetss.data,
  isFetching: state.swPlanetss.isFetching,
  textFilter: state.filters.filterByName.name,
  cColumn: state.filters.filterByNumber.cColumn,
  cOperation: state.filters.filterByNumber.cOperation,
  cNumber: state.filters.filterByNumber.number,
});
const mapDispatchToProps = (dispatch) => ({
  getCurrentPlanets: () => dispatch(fetchPlanets()) });
// Dúvida solucionada no site
// https://stackoverflow.com/questions/38684925/react-eslint-error-missing-in-props-validation
Table.propTypes = {
  getCurrentPlanets: PropTypes.func.isRequired,
  swPlanetss: PropTypes.array.isRequired,
  isFetching: PropTypes.bool.isRequired,
  textFilter: PropTypes.string.isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(Table);
