import React from 'react'
import { connect } from 'react-redux';
import { totalFilter } from '../actions/';

class SelectsInputs extends React.Component {
  constructor(props){
    super(props)
    this.handleCh = this.handleCh.bind(this);
  }
  handleCh(event){
    const {name, value} = event.target;
    this.setState({[name]:value})
  }
  render(){
    const { totalFilter } = this.props
    return (
      <div>
         <label htmlFor="selectColumn">Filtros</label>
         <select data-testid='column-filter' name="Coluna" id="selectColumn" form="selectForm" onChange={(event) => getColumn(event.target.value)}>
          <option>Coluna</option>
          <option value="population">population</option>
          <option value="orbital_period">orbital_period</option>
          <option value="diameter">diameter</option>
          <option value="rotation_period">rotation_period</option>
          <option value="surface_water">surface_water</option>
        </select>
         <br />
         <label htmlFor="selectMaior"></label>
         <select data-testid='comparison-filter'name="Comparação" id='selectMaior' form='selectForm'onChange={(event) => getOperation(event.target.value)}>
           <option>Comparação</option>
           <option value='maior que'>maior que</option>
           <option value='menor que'>menor que</option>
           <option value='igual a'>igual a</option>
         </select>
        <input type='number' data-testid='value-filter' onChange={(event) => getNumber(event.target.value)}/>
        <button  
        value='submit' 
        data-testid='button-filter'
        onClick={() => this.filterTest()}
        >Filtrar</button>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
  getNumber: (a,b,c) => dispatch(dispatch(a,b,c)),
});

export default connect(null, mapDispatchToProps)(SelectsInputs);
