import React from 'react'
import { connect } from 'react-redux';
import filters from '../reducers/filterReducer';
import { numberFilters } from '../actions/';

class SelectsInputs extends React.Component {
  constructor(props){
    super(props);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(e){
    const { column, value } = e.target;
    this.setState({
      [column]:value,
    });
  }

  render(){
    return (
      <div>
         <label htmlFor="selectColumn">Filtros</label>
         <select data-testid='column-filter' name="Coluna" id="selectColumn" form="selectForm" onChange={this.handleChange}>
          <option>Coluna</option>
          <option value="population">population</option>
          <option value="orbital_period">orbital_period</option>
          <option value="diameter">diameter</option>
          <option value="rotation_period">rotation_period</option>
          <option value="surface_water">surface_water</option>
        </select>
         <br />
         <label htmlFor="selectMaior"></label>
         <select data-testid='comparison-filter'name="Comparação" id='selectMaior' form='selectForm'>
           <option>Comparação</option>
           <option value='maior que'>maior que</option>
           <option value='menor que'>menor que</option>
           <option value='igual a'>igual a</option>
         </select>
        <input type='number' data-testid='value-filter' />
        <button type='submit' 
        value='submit' 
        data-testid='button-filter'

        >Filtrar</button>
      </div>
    )
  }
}
const mapStateToProps = (state) => ({
  column: state.column,
  number: state.number,
  operation: state.operation,
});
const mapDispatchToProps = (dispatch) => ({
  getCurrentNFilters: (a,b,c) => dispatch(numberFilters(a,b,c)) });

export default connect(mapStateToProps, null)(SelectsInputs);
