import {
  SEARCH_FILTER, NUMBER_FILTER, COLUMN_FILTER, OPERATION_FILTER,
} from '../actions';

const filterInitial = {
  filterByName: {
    name: '',
  },
  filterByNumber: {
    column: '',
    operation: '',
    number: '',
  }
};
const filters = (state = filterInitial, action) => {
  switch (action.type) {
    case SEARCH_FILTER:
      return {
        ...state,
        filterByName: {
          name: action.payload,
        },
      };
    case NUMBER_FILTER:
      return {
        ...state,
        filterByNumber: {
          ...state.filterByNumber,
          number: action.payload,
        },
      }
    case OPERATION_FILTER:
      return {
        ...state,
        filterByNumber: {
          ...state.filterByNumber,
          operation: action.payload,
        }
      }
    case COLUMN_FILTER:
      return {
        ...state,
        filterByNumber: {
          ...state.filterByNumber,
          column: action.payload,
        }
      }
    default:
      return state;
  }
};


export default filters;
