import React from 'react'
import { connect } from 'react-redux';
import { totalFilter } from '../actions/';

const cOptions = [
  'coluna' ,'population', 'orbital_period', 'diameter', 'rotation_period', 'surface_water',
]

class SelectsInputs extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      coluna: '',
      comparacao: '',
      valor: 0,
    }
    this.handleCh = this.handleCh.bind(this);
  }
  handleCh(event){
    const {name, value} = event.target;
    this.setState({[name]:value})
  }
  render(){
    const { coluna, comparacao, valor } = this.state;
    const { filter } = this.props;
    let fCheck = filter;
    if (fCheck.length > 0){
      fCheck.for
    }

    return (
      <div>
         <label htmlFor="selectColumn">Filtros</label>
         <select data-testid='column-filter' name="coluna" id="selectColumn" form="selectForm" onChange={this.handleCh}>
          {cOptions.map((elements)=> 
            <option value={elements}>{elements}</option>
          )}
         </select>
         <br />
         <label htmlFor="selectMaior"></label>
         <select data-testid='comparison-filter'name="comparacao" id='selectMaior' form='selectForm'onChange={this.handleCh}>
           <option>Comparação</option>
           <option value='maior que'>maior que</option>
           <option value='menor que'>menor que</option>
           <option value='igual a'>igual a</option>
         </select>
        <input type='number' name='valor' data-testid='value-filter' onChange={this.handleCh}/>
        <button
        type = 'button'  
        value='submit' 
        data-testid='button-filter'
        onClick={() => this.props.gFilter(coluna,comparacao,valor)}
        >Filtrar</button>
      </div>
    )
  }
}
const mapStateToProps = (state) => ({
  filter: state.filters.filterByNumericValues,
});
const mapDispatchToProps = (dispatch) => ({
  gFilter: (a,b,c) => dispatch(totalFilter(a,b,c)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SelectsInputs);
