import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchPlanets } from '../actions';
import TableHeaders from './tableheaders';
import TableValues from './tableValues'
import swPlanetss from '../reducers/swPlanets';

class Table extends React.Component {
  constructor (props){
    super(props);
    this.state = {
      renderP: [],
    }
  }
  componentDidMount() {
    const { getCurrentPlanets } = this.props;
    // console.log('antes de chamar')
    getCurrentPlanets();
  }
  filterTest() {
    const { swPlanetss, cColumn, cOperation, cNumber } = this.props;
    let data = swPlanetss;
    if(cColumn !== undefined){
      let tempC = cColumn;
      if(cOperation === 'maior que'){
        console.log('oi')
        data = data.filter((plani) => (plani[tempC]> cNumber))
      }
      else if (cOperation === 'menor que'){
          data.filter ((plani) => (
            plani[cColumn] < cNumber
          ))}
      else if (cOperation === 'igual a'){
            data.filter ((plani) => (
              plani[cColumn] === cNumber
            ))}
    }
    console.log(data)
    console.log (cColumn)
  }

  render() {
    const { swPlanetss, isFetching, textFilter } = this.props;
    const { renderP } = this.state;
