import {
  SEARCH_FILTER, NUMBER_FILTERS,
} from '../actions';

const filterInitial = {
  filterByName: {
    name: '',
  },
  filterByNumber: {
    column: '',
    operation: '',
    number: '',
  }
};
const filters = (state = filterInitial, action) => {
  switch (action.type) {
    case SEARCH_FILTER:
      return {
        ...state,
        filterByName: {
          name: action.payload,
        },
      };
    case NUMBER_FILTERS:
      return {
        filterByNumber: {
          column: action.column,
          operation: action.operation,
          number: action.number,
        },
      }
    default:
      return state;
  }
};


export default filters;
