import {
  SEARCH_FILTER, TOTAL_FILTER,
} from '../actions';

const filterInitial = {
  filterByName: {
    name: '',
  },
  filterByNumber: {
    column: '',
    comparison: '',
    value: 0,
  }
};
const filters = (state = filterInitial, action) => {
  switch (action.type) {
    case SEARCH_FILTER:
      return {
        ...state,
        filterByName: {
          name: action.payload,
        },
      };
    case TOTAL_FILTER:
      return {
        ...state,
        filterByNumber: {
          column,
          comparison,
          value
        }
      }
    default:
      return state;
  }
};


export default filters;
